import React from "react"

function Question(props) {
    var data = props.data
    var correct = data.correct_answer
    var incorrect = data.incorrect_answers
    var options = [...incorrect, correct].sort((a, b) => 0.5 - Math.random())
    var randoptions = options.sort((a,b) => 0.5 - Math.random())
    return (
        <div>
            <h1>Question {props.id + 1}</h1>
            <h3>{data.question}</h3>
            <p>{options}</p>
            <strong>Correct: {correct}</strong>
            
        </div>
    )
}

export default Question;
