import React from "react"
import Quiz from "./Quiz"
import QuizOption from "./QuizOption"
function Question(props) {


    var data = props.data
    var correct = data.correct_answer
    var incorrect = data.incorrect_answers
    var options = [...incorrect, correct].sort((a, b) => 0.49 - Math.random())

    function handleClick(id) {

    }

    return (
        <div className="main-container question">
            <h1>Question {props.id + 1}</h1>
            <h3>{data.question}</h3>
            <div className="question--options">
                {options.map((val,index) => <QuizOption 
                    handleClick={() => handleClick(index)}
                    value={val}
                />)}
            </div>
            
            <strong>Correct: {correct}</strong>
            
        </div>
    )
}

export default Question;
