import React, {useState} from "react";

export default function QuizOption(props) {
    const styles = {
        backgroundColor: 'green'
    }
    const [chosen, setChosen] = useState(false)


    return (
        <div 
            onClick={props.handleClick}
            className={chosen
                ? props.corect ? "option-chosen" : "option-wrong"
                : "option-notchosen"}
        >{props.option}</div>
    )
}
