import React from "react";

export default function QuizOption(props) {
    const styles = {
        backgroundColor: 'green'
    }
    return (
        <div 
            onClick={props.handleClick}
            className={props.chosen ? "option-chosen" : "option-notchosen"}
        >{props.option}</div>
    )
}
