import React, {useState} from "react";

export default function QuizOption(props) {
    const styles = {
        backgroundColor: 'green'
    }
    const [chosen, setChosen] = useState(false)


    function handleClick() {
        setChosen(true)
    }
    return (
        <div 
            onClick={handleClick}
            className={chosen ? "option-chosen" : "option-notchosen"}
        >{props.option}</div>
    )
}
